#!/bin/bash
# shellcheck disable=SC2086
set -ex

VDLM2DEC_STATION_ID='0PIL-KBUR-VDLM2'
VDLM2DEC_RECEIVER='0'
VDLM2DEC_FREQUENCIES='136.650 136.700 136.800 136.725 136.775 136.825 136.875 136.975'
VDLM2DEC_GAIN='280'
VDLM2DEC_INGRESS='feed.acars.io:5555'

if [[ -z $VDLM2DEC_STATION_ID ]]; then
  echo "Edit $0 and set VDLM2DEC_STATION_ID"
  exit 1
fi

if [[ -z $VDLM2DEC_FREQUENCIES ]]; then
  echo "Edit $0 and set VDLM2DEC_FREQUENCIES"
  exit 1
fi

vdlm2dec -J -j ${VDLM2DEC_INGRESS} -i ${VDLM2DEC_STATION_ID} -v -G -E -U -g ${VDLM2DEC_GAIN} -r ${VDLM2DEC_RECEIVER} ${VDLM2DEC_FREQUENCIES} &
